import React from 'react';
import { useGameState } from '../context/GameStateContext';
import RedCards from './RedCards';

function Overlay() {
  const { state } = useGameState();

  const formatTime = (minutes, seconds) => {
    return `${minutes.toString().padStart(2, '0')}:${seconds.toString().padStart(2, '0')}`;
  };

  const formatExtraTime = (extraTime) => {
    return extraTime > 0 ? `+${extraTime}` : '';
  };

  return (
    <div className="overlay-container w-screen h-screen flex items-start justify-center pt-8 bg-transparent">
      <div className="flex items-center justify-center space-x-8">
        {/* Home Team */}
        <div className="flex items-center space-x-4">
          <div className="flex items-center space-x-3">
            {state.homeTeam.logo && (
              <img 
                src={state.homeTeam.logo} 
                alt={state.homeTeam.name}
                className="w-12 h-12 object-contain"
              />
            )}
            <div className="text-white font-bold text-xl">
              {state.homeTeam.name}
            </div>
          </div>
          <RedCards count={state.homeTeam.redCards} />
        </div>

        {/* Score and Timer */}
        <div className="bg-black bg-opacity-80 rounded-lg px-6 py-4 text-center">
          {/* Score */}
          <div className="flex items-center justify-center space-x-4 mb-2">
            <div className="text-white font-black text-4xl">
              {state.homeTeam.score}
            </div>
            <div className="text-white font-light text-2xl">-</div>
            <div className="text-white font-black text-4xl">
              {state.awayTeam.score}
            </div>
          </div>

          {/* Timer */}
          <div className="text-white font-bold text-xl">
            {formatTime(state.timer.minutes, state.timer.seconds)}
          </div>

          {/* Period and Extra Time */}
          <div className="flex items-center justify-center space-x-2 mt-1">
            <span className="text-white font-medium text-sm">
              {state.period}
            </span>
            {state.extraTime > 0 && (
              <span className="text-green-400 font-medium text-sm">
                {formatExtraTime(state.extraTime)}
              </span>
            )}
            {state.timer.isRunning && (
              <div className="w-2 h-2 bg-red-500 rounded-full animate-pulse ml-2"></div>
            )}
          </div>
        </div>

        {/* Away Team */}
        <div className="flex items-center space-x-4">
          <RedCards count={state.awayTeam.redCards} />
          <div className="flex items-center space-x-3">
            <div className="text-white font-bold text-xl">
              {state.awayTeam.name}
            </div>
            {state.awayTeam.logo && (
              <img 
                src={state.awayTeam.logo} 
                alt={state.awayTeam.name}
                className="w-12 h-12 object-contain"
              />
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Overlay;
